sp_widget.client_script:
function($scope) {

	/* widget controller */
	var c = this;

	c.sortType = 'date';
	c.sortReverse = true;

	c.sort = function(field) {
		if (c.sortType == field) {
			c.sortReverse = !c.sortReverse;
		}	else {
			c.sortType = field;
			c.sortReverse = true;
		}
	};

	c.search = '';

}

