sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var brkmgr = '2-hour Early Departure / Late Arrival'; 
	var twohr = 'Breakfast from your Manager';

	if (current.description == brkmgr || current.description == twohr){
		email.addAddress('cc', current.employee.manager.email.toString(), current.employee.manager.name.toString());
	}	

})(current, template, email, email_action, event);
