sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var teamMbrs = [];

    var nomRecs = new GlideRecord('x_thimi_imt_rec_imt_recognition');
	nomRecs.addQuery('participation_record', current);
	nomRecs.orderBy('name');
	nomRecs.query();
	while(nomRecs.next()) {
		teamMbrs.push(nomRecs.employee.name);
	}

	template.print(teamMbrs.join(', '));

})(current, template, email, email_action, event);
